import React, { useState } from 'react';
import { FetchMealByFirstLetter } from '../services/mealtService';


function Home() {
	const [mealImage, setMealImage] = useState(null);
	const [searchTerm, setSearchTerm] = useState('');

	const handleSearch = async () => {
		const meals = await FetchMealByFirstLetter(searchTerm);


		if (meals && meals.length > 0) {
			setMealImage(meals[0].strMealThumb); // Establece la imagen de la primera comida
		} else {
			setMealImage(null); // Resetea si no hay comidas
		}
	};
	return (
		<div>
			<h1>Buscador de Recetas</h1>
			<input
				type="text"
				placeholder="Buscar una receta"
				value={searchTerm}
				onChange={(e) => setSearchTerm(e.target.value)}
			/>
			<button onClick={handleSearch}>Buscar</button>

			{mealImage && <img src={mealImage} alt="Meal" />}
		</div>
	);
}

export default Home;
