import React from 'react';
import { render, screen, fireEvent } from '@testing-library/react';
import '@testing-library/jest-dom';
import Navbar from './Navbar';

// Mock del contexto: simula el contexto para evitar la implementaci칩n real
jest.mock('../services/Context', () => ({
  useRecipeContext: () => ({
    searchRecipes: jest.fn(), // Se simula la funci칩n searchRecipes
  }),
}));

// Pruebas para el componente Navbar
describe('Navbar Component', () => {

  // Verifica que el componente se renderiza correctamente
  test('renders correctly', () => {
    render(<Navbar />);

    // Verifica que el 칤cono de inicio (游) est치 presente en el componente
    expect(screen.getByText('游')).toBeInTheDocument();

    // Verifica que el campo de b칰squeda est치 presente
    expect(screen.getByPlaceholderText('Search for a meal...')).toBeInTheDocument();
  });

  // Verifica que el campo de b칰squeda actualiza su valor y llama a la funci칩n de b칰squeda
  test('updates search term and calls searchRecipes on input change', () => {
    const { searchRecipes } = require('../services/Context').useRecipeContext(); // Obtiene la funci칩n mockeada
    render(<Navbar />);

    const searchInput = screen.getByPlaceholderText('Search for a meal...'); // Encuentra el campo de b칰squeda

    // Simula que el usuario escribe "pizza" en el campo de b칰squeda
    fireEvent.change(searchInput, { target: { value: 'beef' } });

    // Verifica que el valor del campo de b칰squeda se actualiz칩 a "pizza"
    expect(searchInput.value).toBe('beef');

    // Verifica que la funci칩n searchRecipes fue llamada con el t칠rmino "pizza"
    expect(searchRecipes).toHaveBeenCalledWith('beef');
  });
});
