import { createContext, useState } from "react";
import { FetchMealByFirstLetter, SearchMealByName } from "./mealtService";

const recipesContext = createContext();

export const recipesProvider = ({ children }) =>{
    const [recipes, setRecipes] = useState([]);


    const searchrecipes = async (name) => {
        const results = await SearchMealByName(name);
        if (results) {
          setRecipes(results);
        } else {
          setRecipes([]);
        }
      };


      const loadRecipes = async () => {
        const results = await FetchMealByFirstLetter(); 
        if (results) {
          setRecipes(results);
        }

      };
      useEffect(() => {
        loadAllMeals();
      }, []);

      return (
        <recipesContext.Provider value={{ recipes, searchrecipes, loadRecipes }}>
          {children}
        </recipesContext.Provider>
      );
}

export const useRecipeContext = () => {
    return useContext(recipesContext);
  };
