import { useRecipeContext } from './RecipesProvider';

const MealDetails = ({ mealId }) => {
  const { mealDetails, fetchMealDetails } = useRecipeContext();

  useEffect(() => {
    if (mealId) {
      fetchMealDetails(mealId); // Cargar los detalles de la comida por ID
    }
  }, [mealId]);

  if (!mealDetails) {
    return <p>Loading meal details...</p>;
  }

  return (
    <div>
      <h2>{mealDetails.name}</h2>
      <p>{mealDetails.instructions}</p>
      <img src={mealDetails.image} alt={mealDetails.name} />
    </div>
  );
};


export default MealDetails;
