import "./App.css";
import Navbar from "./components/Navbar";
Home
import RecipeDetails from "./pages/RecipeDetails"; // Asegúrate de importar el componente de detalles
import { RecipesProvider } from "./services/Context";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom"; // Importamos React Router

function App() {
  return (
    <RecipesProvider>
      <Router> {/* Envolvemos la aplicación en el Router */}
        <div className="App">
          <Navbar />

          {/* Configuramos las rutas de la aplicación */}
          <Routes>
            <Route path="/" element={<Home />} /> {/* Página principal */}
            <Route path="/details/:mealId" element={<RecipeDetails />} /> {/* Página de detalles */}
          </Routes>
        </div>
      </Router>
    </RecipesProvider>
  );
}

export default App;
