import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Para obtener el ID de la comida
import { useRecipeContext } from '../services/Context'; // Asegúrate de la ruta correcta

const MealDetails = () => {
  const { mealId } = useParams(); // Obtener el mealId de los parámetros de la ruta
  const { mealDetails, fetchMealDetails } = useRecipeContext();

  useEffect(() => {
    if (mealId) {
      fetchMealDetails(mealId); // Cargar los detalles de la comida por ID
    }
  }, [mealId, fetchMealDetails]); // Agrega fetchMealDetails a las dependencias

  if (!mealDetails) {
    return <p>Loading meal details...</p>;
  }

  return (
    <div className="details-container">
      <h2>{mealDetails.strMeal}</h2>
      <div class="details">

        <img src={mealDetails.strMealThumb} alt={mealDetails.name} />
        <p>{mealDetails.strInstructions}</p>
      </div>

    </div>
  );
};

export default MealDetails;
